import React, { useEffect, useState } from 'react'
import './Skills.scss'
import Animation from '../Animation/Animation'
import Loader from 'react-loaders'

const Skills = () => {

    const [letterClass, setLetterClass] = useState('text-animate')

    const array = ['S', 'k', 'i', 'l', 'l', 's']

    const skills = [
        { href: '#git_', title: 'Git' },
        { href: '#json_', title: 'JSON' },
        { href: '#solidity_', title: 'Solidity' },
        { href: '#html_', title: 'HTML' },
        { href: '#react_', title: 'React' },
        { href: '#python_', title: 'Python' },
    ];

    useEffect(() => {
        setTimeout(() => {
            setLetterClass('text-animate-hover')
        }, 3000)
    }, [])

    useEffect(() => {
        const TagCanvas = window.TagCanvas;
        const tagCanvasOptions = {
            textColour: textCol,
            outlineThickness: 0.5,
            outlineColour: "#FE0853",
            maxSpeed: 0.06,
            freezeActive: true,
            shuffleTags: true,
            shape: "sphere",
            zoom: 0.9,
            noSelect: true,
            textFont: null,
            pinchZoom: true,
            freezeDecel: true,
            fadeIn: 3000,
            initial: [0.3, -0.1],
            depth: 1.4,
        };
        try {
            TagCanvas.Start('tagcanvas', 'taglist', tagCanvasOptions);
        } catch (e) {
            console.log('Canvas error.');
            console.log(e);
        }
    }, [])

    return (
        <>
            <div className='skills container'>
                <div className='left'>
                    <span className='tag'>&lt;h1&gt;</span>
                    <h1>
                        <Animation
                            letterClass={letterClass}
                            strArray={array}
                            index={8}
                        />
                    </h1>
                    <span className='tag'>&lt;/h1&gt;</span>
                    <br />
                    <span className='tag'>&lt;p&gt;</span>
                    <p>

                    </p>
                    <span className='tag'>&lt;/p&gt;</span>
                </div>
                <div className='right'>
                    <div id="myCanvasContainer">
                        <canvas width="300" height="300" id="myCanvas"></canvas>
                        <div id='taglist' style={{ display: 'none' }}>
                            <ul>
                                {skills.map((skill) => (
                                    <li key={skill.title}>
                                        <a href={skill.href}>{skill.title}</a>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
            <Loader type='square-spin' />
        </>
    )
}

export default Skills
