import React, { useEffect, useState } from 'react'
import './Home.scss'
import { Link } from 'react-router-dom'
import Animation from '../Animation/Animation'

const Home = () => {
    const [letterClass, setLetterClass] = useState('text-animate')

    const array1 = ['H', 'i', ',']
    const array2 = ['I', '\'', 'm', ' ', 'R', 'a', 'j', 'a', 't']
    const array3 = ['f', 'r', 'o', 'n', 't', 'e', 'n', 'd', ' ', 'd', 'e', 'v', 'e', 'l', 'o', 'p', 'e', 'r']

    useEffect(() => {
        setTimeout(() => {
            setLetterClass('text-animate-hover')
        }, 6000)
    }, [])

    let animElm = document.querySelector(".anim");
    let slideElm = document.querySelector(".slide");

    slideElm.addEventListener("mouseenter", enterCall);
    slideElm.addEventListener("mouseleave", leaveCall);

    let enterAnim = gsap.timeline();
    enterAnim.to(animElm, { left: "0%", duration: 0.5 });
    enterAnim.reversed(true);

    let leaveAnim = gsap.timeline();
    leaveAnim.to(animElm, { left: "100%", duration: 0.5 });
    leaveAnim.reversed(true);

    function enterCall() {
        // restart (play) when tween is not active
        if (!enterAnim.isActive()) enterAnim.restart();
    }
    function leaveCall() {
        // restart (play) when tween is not active
        if (!leaveAnim.isActive()) leaveAnim.restart();
    }

    return (
        <div className='home container'>
            <div className='title'>
                <span className='tag'>&lt;h1&gt;</span>
                <h1>
                    <Animation
                        letterClass={letterClass}
                        strArray={array1}
                        index={20}
                    /><br />
                    <Animation
                        letterClass={letterClass}
                        strArray={array2}
                        index={23}
                    /><br />
                    <Animation
                        letterClass={letterClass}
                        strArray={array3}
                        index={32}
                    /><br />
                </h1>
                <span className='tag'>&lt;/h1&gt;</span>
                <p>Web Developer / Full Stack Developer / Freelancer</p>
                <div className='buttons'>
                    <Link exact='true' to='/'>RESUME</Link>
                    <Link exact='true' to='/contact'>CONTACT ME</Link>
                </div>
            </div>
        </div>
    )
}

export default Home
