import React, { useEffect, useState } from 'react'
import './Skills.scss'
import Animation from '../Animation/Animation'
import Loader from 'react-loaders'

const Skills = () => {

    const [letterClass, setLetterClass] = useState('text-animate')

    const array = ['S', 'k', 'i', 'l', 'l', 's']

    const skills = [
        { href: 'git', title: 'Git' },
        { href: 'json', title: 'JSON' },
        { href: 'html', title: 'HTML' },
        { href: 'react_(software)', title: 'React' },
        { href: 'python', title: 'Python' },
    ];

    useEffect(() => {
        setTimeout(() => {
            setLetterClass('text-animate-hover')
        }, 3000)
    }, [])

    useEffect(() => {
        const TagCanvas = window.TagCanvas;
        const tagCanvasOptions = {
            outlineColour: '#0092ca',
            textColour: '#eeeeee',
            maxSpeed: 0.06,
            freezeActive: true,
            shuffleTags: true,
            shape: 'sphere',
            zoom: 0.8,
            wheelZoom: true,
            noSelect: false,
            textFont: null,
            freezeDecel: true,
            fadeIn: 3000,
            initial: [0.6, -0.2],
            depth: 1.1,
        };
        try {
            TagCanvas.Start('myCanvas', 'taglist', tagCanvasOptions);
        } catch (e) {
            console.log('Canvas error.');
            console.log(e);
        }
    }, [])

    return (
        <>
            <div className='skills container'>
                <div className='left'>
                    <span className='tag'>&lt;h1&gt;</span>
                    <h1>
                        <Animation
                            letterClass={letterClass}
                            strArray={array}
                            index={8}
                        />
                    </h1>
                    <span className='tag'>&lt;/h1&gt;</span>
                    <br />
                    <span className='tag'>&lt;p&gt;</span>
                    <p>

                    </p>
                    <span className='tag'>&lt;/p&gt;</span>
                </div>
                <div className='right'>
                    <div className='canvasContainer'>
                        <canvas id='myCanvas'></canvas>
                        <div id='taglist' style={{ display: 'none' }}>
                            <ul>
                                {skills.map((skill) => (
                                    <li key={skill.title}>
                                        <a
                                            href={`https://en.m.wikipedia.org/wiki/${skill.href}`}
                                            target='_blank'
                                            rel='noreferrer'
                                        >{skill.title}</a>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
            <Loader type='square-spin' />
        </>
    )
}

export default Skills
