import React, { useEffect, useState } from 'react'
import './Home.scss'
import { Link } from 'react-router-dom'
import Animation from '../Animation/Animation'

const Home = () => {
    const [letterClass, setLetterClass] = useState('text-animate')

    const array1 = ['H', 'i', ',']
    const array2 = ['I', '\'', 'm', ' ', 'R', 'a', 'j', 'a', 't']
    const array3 = ['f', 'r', 'o', 'n', 't', 'e', 'n', 'd', ' ', 'd', 'e', 'v', 'e', 'l', 'o', 'p', 'e', 'r', '.']

    useEffect(() => {
        setTimeout(() => {
            setLetterClass('text-animate-hover')
        }, 5000)
    }, [])

    return (
        <div className='home container'>
            <div className='left'>
                <span className='tag'>&lt;h1&gt;</span>
                <h1>
                    <Animation
                        letterClass={letterClass}
                        strArray={array1}
                        index={12}
                    /><br />
                    <Animation
                        letterClass={letterClass}
                        strArray={array2}
                        index={15}
                    /><br />
                    <Animation
                        letterClass={letterClass}
                        strArray={array3}
                        index={24}
                    /><br />
                </h1>
                <span className='tag'>&lt;/h1&gt;</span>
                <h4>Web Developer / Full Stack Developer / Freelancer</h4>
                <div className='buttons'>
                    <Link className='btn' exact='true' to='./assets'>RESUME</Link>
                    <Link className='btn' exact='true' to='/contact'>CONTACT ME</Link>
                </div>
            </div>
        </div>
    )
}

export default Home
